import * as l from "./layout";

export const colors = {
  background: "#1C1C1C",
  accent: "#6872DE",
  white: "#FFFFFF",
  gray: "#8D8D8D",
  box: "#272727",
  loader_bg: "rgba(0, 0, 0, 0.60)",
};

export const sizes = {
  title: 44,
  small: l.setSize(11.5, 10, 8), //l.setSize(11.5, 10, 8)
  regular: l.setSize(15, 13, 11),
  header: 22,
  base4: 4,
  base8: 8,
  base12: 12,
  base16: 16,
  base20: 20,
  base24: 24,
  base28: 28,
  base32: 32,
  rowHeight: 60,
  contentWidth: "96%",
};

export const fonts = {
  med: "MARKPRO-MED",
  bold: "MARKPRO-BOLD",
};
